import axios from 'axios';
import React, { createContext, useState, useEffect } from 'react'

export const Context = createContext();

export const DataProvider = ({ children }) => {
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState([]);
    const [categories, setCategory] = useState([]);
    const [total, setTotal] = useState(0);
    const [dataSearch, setDataSearch] = useState([]);
    const [inputSearch, setInputSearch] = useState('')

    useEffect(() => {
        const ListProduct = async () => {
            await axios("http://localhost:8080")
                .then(result => {
                    setProducts(result.data);
                },
                    (error) => {
                        console.log(error);
                    }
                )
        }
        ListProduct()

        const ListCategory = async () => {
            await axios('http://localhost:8080/category')
                .then(result => {
                    setCategory(result.data);
                },
                    (err) => {
                        console.log(err);
                    })
        };
        ListCategory();

        const dataCart = JSON.parse(localStorage.getItem('dataCart'));
        if (dataCart !== null) {
            setCart(dataCart);
        }
        const dataTotal = JSON.parse(localStorage.getItem('dataTotal'));
        if (dataTotal !== null) {
            setTotal(dataTotal);
        }
    }, [])

    const addCart = (id) => {
        console.log(products)
        alert("Thêm thành công!")
        const check = cart.every(item => {
            return item.id !== id;
        })
        if (check) {
            const data = products.filter(product => {
                return product.id === id;
            })
            setCart([...cart, ...data])
        } else {
            increase(id)
        }
    }

    const reduction = id => {
        cart.forEach(item => {
            if (item.id === id) {
                // if(item.count <= 1) {
                //     removeProduct(id)
                // }else {
                //     item.count -= 1
                // }
                item.count <= 1 ? removeProduct(id) : item.count -= 1;

            }
        })
        setCart(cart)
        getTotal()
    }

    const increase = id => {
        cart.forEach(item => {
            if (item.id === id) {
                item.count += 1;
            }
        })

        setCart(cart)
        getTotal()
    }

    const removeProduct = id => {
        if (window.confirm('Are you sure you want to remove Product?')) {
            cart.forEach((item, index) => {
                if (item.id === id) {
                    cart.splice(index, 1);
                }
            })
            setCart(cart)
            getTotal()
        }
    }

    const getTotal = () => {
        const res = cart.reduce((prev, item) => {
            return prev + (item.count * item.price);
        }, 0)
        setTotal(res)
    }

    useEffect(() => {
        localStorage.setItem('dataCart', JSON.stringify(cart));
        localStorage.setItem('dataTotal', JSON.stringify(total));
    })

    const requestSearch = (e) => {
        const inputSearch = e.target.value.toLowerCase();
        setInputSearch(inputSearch)

        // const filterSearch = products.filter((dataSearch) => {
        //     return dataSearch.name.toLowerCase().includes(inputSearch.toLowerCase());
        // });
        // inputSearch !== " " ? setDataSearch(filterSearch) : setDataSearch([]);
        // console.log(inputSearch)
        // console.log(dataSearch)
        useEffect(() => {  
        const filterSearch = products.filter((dataSearch) => {
            return dataSearch.name.toLowerCase().includes(inputSearch.toLowerCase());
        });
        inputSearch !== " " ? setDataSearch(filterSearch) : setDataSearch([]);
    },[inputSearch])
    };

    
    
    const cancelSearch = () => {
        setInputSearch('')
        requestSearch(inputSearch)     
    }
    // console.log(cancelSearch())
    console.log(dataSearch)
    console.log(inputSearch)

    const value = { products, categories, addCart, cart,
        reduction, increase, removeProduct, total, getTotal,
        requestSearch, inputSearch, dataSearch, cancelSearch };

    return (
        <Context.Provider value={value}> {children} </Context.Provider>
    )

}
