import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import { useForm } from "react-hook-form";
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Menu from "../home/Menu";
import "../css/Login.css"

function Edit() {
    const { register, handleSubmit, formState: { errors } } = useForm();
    const [ name, setName ] = useState('');
    const [ image, setImage ] = useState('');
    const [ price, setPrice ] = useState('');
    const [ category, setCategory ] = useState('');
    const [ desciption, setDesciption ] = useState('');
    
    let params = useParams();
    console.log(params)

    const getProductDetails = async () => {
        let result = await fetch(`http://localhost:8080/editProduct/${params.id}`);
        result = await result.json();
        console.log(result);
        setName(result.name)
    }
    useEffect(() => {
        getProductDetails();
        // eslint-disable-next-line
    }, [])

    const [data, setData] = useState([]);
    const onSubmit = data => {
        console.log(data)
        var config = {
            method: "put",
            url: "http://localhost:8080/editProduct/" + params.id,
            data: data
        };
        console.log(config)
        axios(config)
            .then(res => res.json())
            .then(
                (result) => {

                    setData(result);
                    console.log(data);
                    //console.log(result);
                },
                (error) => {
                    console.log(error);
                }
            )
    }
    
    
    
    return (
        <div className="container" id="container">
            <div className="form-container sign-in-container">
                
                    <h1>Sửa sản phẩm</h1>
                    <a href="/admin/products/1">Back</a>

                    <input type="text" name="name" className="form-control" value={name} onChange={(e) => {setName(e.target.value)}} placeholder="Nhập tên" />
                    
                    <input type="number" className="form-control" placeholder="Nhập giá" value={price} onChange={(e) => {setPrice(e.target.value)}}/>
                    
                    <input type="text" className="form-control" placeholder="Link ảnh" id="Role" value={image} onChange={(e) => {setImage(e.target.value)}}/>

                    <select {...register("idcategory")}>
                        <option >1</option>
                        <option >2</option>
                        <option >3</option>
                    </select>

                    <input type="text" className="form-control" placeholder="Mô tả" id="Role" {...register("desciption")} />

                    <button type="submit">Sửa</button>
                
            </div>

            
        </div>
    )
}

export default Edit;
