import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import { useForm } from "react-hook-form";
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Menu from "../home/Menu";
import "../css/Login.css"

function Edit() {
    const { register, handleSubmit, formState: { errors } } = useForm();

    let { id } = useParams();
    console.log(id)

    const [data, setData] = useState([]);
    const [category, setCategory] = useState([]);

    const onSubmit = data => {
        console.log(data);

        const getDataForm = {
            id: data.id,
            idcategory: data.idcategory,
            name: data.name,
            price: data.price,
            image: data.image,
            image: picture.picture,
            description: data.description,
        }
        console.log(getDataForm.image)
        axios.post("http://localhost:8080/editproduct", getDataForm)
            .then(function (res) {
                console.log(res)
                alert("Sửa thành công!");
                window.location.assign('http://localhost:3000/admin/products/1');
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    //  getdata category
    useEffect(() => {
        const fetchData = async () => {
            await axios.get("http://localhost:8080/category")
                .then(result => {
                    console.log(result);
                    setCategory(result.data);
                },
                    (error) => {
                        console.log(error);
                    }
                )
        };
        fetchData();

        // load data from
        const dataForm = async () => {
            await axios.get("http://localhost:8080/detail/" + id)
                .then(result => {
                    console.log(result);
                    setData(result.data);
                    
                },
                    (error) => {
                        console.log(error);
                    }
                )
        };
        dataForm();
    }, []);

    // load image
    const [picture, setPicture] = useState({
        picture: ``
    })
    const onChangeIMG = async (event) => {
        const target = event.target.files[0]
        const base64 = await convertBase64(target);
        setPicture({ picture: `${base64}` })
        console.log(picture.picture);

    };
    const convertBase64 = async (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => resolve(reader.result);
            reader.onerror = (e) => reject(e);
        })

    }


    const renderData = (data) => {
        console.log(data)
        return (
            <main>
                {data.map((todo, i) => {
                    return (
                        <div key={i} className="row">
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="first">ID sản phẩm</label>
                                    <input disabled {...register("id", {required: "Không được để trống!",})} type="text" className="form-control"
                                        id="id" value={todo.id}/>
                                </div>
                            </div>

                            {/* end */}
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="first">Tên sản phẩm</label>
                                    <input {...register("name", { required: "Không được để trống!",})}
                                        type="text" className="form-control" id="last" defaultValue={todo.name}
                                    />
                                </div>
                            </div>
                            {/* end */}
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="company">Giá bán</label>
                                    <input {...register("price", {required: "Không được để trống!",pattern: {value: /\d+/, message: "Phải nhập số",},})}
                                        className="form-control" id="company" defaultValue={todo.price} />
                                </div>
                            </div>

                            {/* end */}

                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="email">Hãng sản xuất</label>
                                    <select {...register("idcategory", {required: "Chưa chọn hãng sản xuất!",})} className="form-control">
                                        {category.map((cate, index) => {
                                            return (
                                                <option key={index} value={todo.idcategory}>{cate.name}</option>
                                                
                                            );
                                            
                                        })}

                                    </select>
                                </div>
                            </div>

                            {/* end */}
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label htmlFor="url">
                                        Your Website <small>Please include http://</small>
                                    </label>
                                    <input {...register("images", {required: "Không được để trống!",})}
                                        type="text" className="form-control" id="url" placeholder="url" defaultValue={todo.image}
                                    />
                                    
                                    <label htmlFor="exampleFormControlFile1">
                                        File (jpg/png):
                                    </label>
                                    <input className="img" type="file" onChange={onChangeIMG} />
                                </div>
                            </div>
                        </div>
                    )
                })}
            </main>
        );
    };

    const renderCategory = (category) => {
        return (
            <div>
                {category.map((cate, index) => {
                    return <option value={cate.idcategory}>{cate.name}</option>;
                })}
            </div>
        );
    };

    var items = [];
    for (var i = 0; i < category.length; i++) {
        items.push(
            <option value={category[i].idcategory}>{category[i].name}</option>
        );
    }

    return (
        <div>
            <h2 style={{ textAlign: "center", marginTop: "10px", color: "green" }}>
                Sửa sản phẩm
            </h2>
            <div className="container">
                <form onSubmit={handleSubmit(onSubmit)}>
                    {renderData(data)}
                    {/* <!--  row   --> */}

                    <button type="submit" className="btn btn-success" style={{ float: "right" }}>
                        Sửa
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Edit;
