import "../cart/css/Cart.css";
import "../../App.css";
import { Link } from "react-router-dom";
import React, { useContext, useEffect } from "react";
import Menu from "../home/Menu";
import { Context } from "../../contexts/Context";

const Cart = () => {
  const { cart, reduction, increase, removeProduct, total, getTotal } = useContext(Context)
  useEffect(() => {
    getTotal();
  }, [])

  return (
    <div>
      <Menu />
      <main className="main">
        {
          cart.map(item => (
            <div className="" key={item.id}>
              {/* <img src={item.image} alt="" /> */}
              <div className="">
                <div className="">
                  <h2>{item.name}</h2>
                  <span>{}</span>
                  <CurrencyFormat value={item.price * item.count} displayType={'text'} thousandSeparator={true} suffix={' ₫'} renderText={value => 
                      <span>{value}</span>} />
                </div>
                

                <p>{item.description}</p>
                <p>{item.content}</p>
                <div className="">
                  <button className="count" onClick={() => reduction(item.id)}> - </button>
                  <span>{item.count}</span>
                  <button className="count" onClick={() => increase(item.id)}> + </button>
                </div>
              </div>
              <div className="" onClick={() => removeProduct(item.id)}>X</div>
            </div>
          ))
        }
        <div className="total">
          <Link to="/payment">Payment</Link>
          <h3>Total: ${total}</h3>
        </div>

      </main>
    </div>
  );
}

export default Cart;
