import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import {
    Card,
    CardBody,
    CardTitle,
    CardSubtitle,
    Button,
    Table,
    Modal,
    ModalBody,
    Input,
    FormGroup,
    Label,
} from "reactstrap";
import Menu from "../home/Menu";
import "../dashboard/css/Admin.css";
import { useForm } from 'react-hook-form';
function Admin() {

    const [data, setData] = useState([]);
    const [tongtrang, setTongtrang] = useState(0);
    const { register, handleSubmit, formState: { errors } } = useForm();


    const [category, setCategory] = useState([]);
    const [deleteId, setDeleteId] = useState(0);
    const [editId, setEditId] = useState(0);
    const [name, setName] = useState("");
    const [open, setOpen] = useState(false);
    const [openDelete, setOpenDelete] = useState(false);

    var items = [];
    let { numberpage } = useParams();
    useEffect(() => {
        const fetchData = async () => {
            await axios("http://localhost:8080/admin/products/" + numberpage)
                .then(result => {
                    console.log(result);
                    setData(result.data);
                },
                    (error) => {
                        console.log(error);
                    }
                )
        };
        fetchData();

        /*tonguser*/

        fetch("http://localhost:8080/countproducts")
            .then(res => res.json())
            .then(
                (result) => {

                    setTongtrang(Math.ceil(result[0].total / 10));

                },
                (error) => {
                    console.log(error);
                }
            )
    }, [])


    function Pagination() {
        console.log(tongtrang);
        var items = []

        for (var i = 1; i <= tongtrang; i++) {
            items.push(
                <li key={i} className="page-item"><a className="page-link" href={'/admin/products/' + i}>{i}</a></li>

            );
        }
        return (
            <nav aria-label="Page navigation example">
                <ul className="pagination">
                    {items}
                </ul>
            </nav>
        )

    }

    const addProduct = (data) => {
        console.log(data);
        var config = {
            method: "post",
            url: "http://localhost:8080/add",
            data: data
        };
        axios(config)
            .then(function (response) {
                console.log(response);

                window.location.assign('http://localhost:3000/admin/products/1');
                if (response.data == "Thành Công") {

                }


            })
            .catch(function (error) {
                console.log(error);
            });
        console.log(data)
    }

    function DeleteProduct(id, e) {
        console.log(id)

        if (window.confirm("Bạn có muốn xóa?")) {
            fetch("http://localhost:8080/delete/" + id)
                .then(res => res.json())
                .then(
                    (result) => {
                        window.location.reload(true);
                    },
                    (error) => {
                        console.log("error", error);
                    }
                )
        }

    }

    return (


        <div>
            <Card>
                <CardBody>
                    <CardTitle tag="h5">Danh Sách Sản Phẩm</CardTitle>
                    <div className="d-flex">
                        <CardSubtitle className="mb-2 text-muted me-auto" tag="h6">
                            Tất cả sản phẩm có trong cửa hàng
                        </CardSubtitle>

                        <Button
                            onClick={() => setOpen(true)}
                            className="btn"
                            color="primary"
                        >
                            Thêm Danh Mục
                        </Button>
                    </div>
                    <Table className="no-wrap mt-3 align-middle" responsive borderless>
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Ảnh</th>
                                <th scope="col">Tên sản phẩm</th>
                                <th scope="col">Giá</th>
                                <th scope="col">Loại</th>
                                <th scope="col">Mô tả</th>
                                <th scope="col">Thao tác</th>

                            </tr>
                        </thead>
                        <tbody>
                            {data.map((item, index) => {
                                return (
                                    <tr key={index} className="border-top">
                                        <th scope="row">{item.id}</th>
                                        <td>
                                            <img
                                                src={item.image}
                                                style={{ width: "100px", height: "100px" }}
                                            ></img>
                                        </td>
                                        <td>{item.name}</td>
                                        <td>{item.price}</td>
                                        <td>{item.category}</td>
                                        <td className="description">{item.description}</td>
                                        <td>
                                            <button
                                                onClick={() => {
                                                    setOpen(true);
                                                    setEditId(item.id);
                                                    setName(item.name);
                                                }}
                                                type="button"
                                                class="btn btn-success"
                                            >
                                            </button>
                                            <button className="button-one">
                                                <Link to={`/editProduct/${item.id}`}>
                                                    <i className="ic1 fas fa-edit">edit</i> </Link>
                                            </button>

                                            <button
                                                onClick={() => {
                                                    setOpenDelete(true);
                                                    setDeleteId(item.id);
                                                }}
                                                type="button"
                                                class="btn btn-danger"
                                            >
                                                {/* <BsTrash /> */}
                                            </button>
                                            <button className="button-one" onClick={(e) => DeleteProduct(item.id)}>
                                                <span>
                                                    <i className="ic1 far fa-trash-alt"></i>
                                                </span>
                                            </button>
                                        </td>
                                    </tr>
                                );
                            })}

                        </tbody>
                        
                    </Table>
                </CardBody>
            </Card>
            <Modal isOpen={open} toggle={() => setOpen(false)}>
                <ModalBody>
                    <FormGroup>
                        <h1>Thêm sản phẩm</h1>

                        <input type="text" name="name" className="form-control" placeholder="Nhập tên"{...register("name", { required: true, maxLength: 20 })} />
                        <h style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.name && "Tên không để trống và quá 20 kí tự!"}</h>

                        <input type="number" className="form-control" placeholder="Nhập giá" {...register("price", { required: true, maxLength: 20 })} />
                        <h style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.price && "Giá không hợp lệ!"}</h>

                        <input type="text" className="form-control" placeholder="Link ảnh" id="Role" {...register("image")} />
                        <h style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.image && "Không hợp lệ!"}</h>

                        <select {...register("idcategory")}>
                            <option >iPhone</option>
                            <option >SamSung</option>
                            <option >Realme</option>
                            <option >OPPO</option>
                        </select>

                        <input type="text" className="form-control" placeholder="Mô tả" id="Role" {...register("description")} />

                    </FormGroup>
                    <Button onClick={addProduct} className="btn" color="primary">
                        Thêm Danh Mục
                    </Button>
                </ModalBody>
            </Modal>
            {/* <Modal isOpen={openDelete} toggle={() => setOpenDelete(false)}>
                <ModalBody>
