import React, { useState, useEffect } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import Menu from "../home/Menu";
import "../css/Admin.css";
// import Edit from "../Reactjs/Edit";
function Admin() {

    const [data, setData] = useState([]);
    const [tongtrang, setTongtrang] = useState(0);
    var items = [];
    let { numberpage } = useParams();
    useEffect(() => {
        fetch("http://localhost:8080/products/1")
            .then(res => res.json())
            .then(
                (result) => {

                    setData(result);
                    console.log(data);
                    //console.log(result);
                },
                (error) => {
                    console.log("error", error);
                }
            )
        /*tonguser*/
        fetch("http://localhost:8080/countproducts")
            .then(res => res.json())
            .then(
                (result) => {

                    setTongtrang(Math.ceil(result[0].total / 10));

                },
                (error) => {
                    console.log("error");
                }
            )
    }, [])


    function Pagination() {
        console.log(tongtrang);
        var items = []

        for (var i = 1; i <= tongtrang; i++) {
            items.push(
                <li key={i} className="page-item"><a className="page-link" href={'/phonepage/' + i}>{i}</a></li>

            );
        }
        return (
            <nav aria-label="Page navigation example">
                <ul className="pagination">
                    {items}
                </ul>
            </nav>
        )

    }


    const renderData = (data) => {
        return (
            <tbody>
                {data.map((todo, index) => {
                    return (
                        <tr key={index}>
                            <th scope="row">{todo.id}</th>
                            <td>
                                <img
                                    src={todo.image}
                                    style={{ width: "100px", height: "100px" }}
                                ></img>
                            </td>
                            <td>{todo.name}</td>
                            <td>{todo.price}</td>
                            <td>
                                <button className="button-one">
                                    <i className="ic1 fas fa-edit"></i>
                                </button>
                                <button className="button-one" onClick={(e) => DeleteProduct(todo.id)}>
                                    <span>
                                        <i className="ic1 far fa-trash-alt"></i>
                                    </span>
                                </button>
                            </td>
                        </tr>
                    );
                })}

            </tbody>
        );
    };

    renderItem = () => {
        let { items, idEdit, name, price, image, idcategory, description } = this.state;
        if (items.length === 0) {
            return <Item item={0} />
        }
        return mapld(items, (item, index) => {
            if (item.id === idEdit) {
                return (
                    <Edit
                        name={name}
                        image={image}
                        price={price}
                        idcategory={idcategory}
                        description={description}
                    />
                )
            }
            return (
                <Item
                    key={index}
                />
            )
        });
    }
    handleEditItem = (index, item) => {
        console.log(index, item);
        this.setState({
            name: item.name,
            price: item.price,
            image: item.image,
            idcategory: item.idcategory,
            description: item.description,
        });
    }

      

    function DeleteProduct(id, e) {
        console.log(id)

        if (window.confirm("Bạn có muốn xóa?")) {
            fetch("http://localhost:8080/delete/" + id)
                .then(res => res.json())
                .then(
                    (result) => {
                        window.location.reload(true);
                    },
                    (error) => {
                        console.log("error", error);
                    }
                )
        }

    }

    return (


        <div>
            <Menu />
            <div className="btnAdd">
                <a href="/Add">Thêm</a>
            </div>
            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">Ảnh</th>
                        <th scope="col">Tên sản phẩm</th>
                        <th scope="col">Giá</th>
                        <th scope="col">Thao tác</th>

                    </tr>
                </thead>
                {renderData(data)}
            </table>
            <Pagination />
        </div>
    );
}

export default Admin;
