import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Link,
  useParams
} from "react-router-dom";
import CurrencyFormat from 'react-currency-format';
import Footer from "../home/Footer";
import Menu from '../home/Menu';

export const ProductsCate = () => {
  const [products, setProducts] = useState([]);
  console.log(products)

  let { id } = useParams();
  useEffect(() => {
    const ProductsCate = async () => {
      await axios("http://localhost:8080/products/category/" + id)
        .then(response => {
          setProducts(response.data);
        }, err => {
          console.log(err);
        })
    };
    ProductsCate();
  }, [])

  return (
    <div className="Container">
      <div className="menu">
        <Menu />
      </div>
      <div className="products">
        <div className="khuyenMaiHot">
          <div className="nameS">
            <span>Khuyáº¿n mÃ£i hot</span>
          </div>
        </div>
        <div className="list-pro">
            {products.map((item) => {
              <div className="product_item" key={item.id}>
              <div className="items">
                  <Link to={'/detailPro/' + item.id} >
                      <p><img src={item.image} className="App-logo" alt="logo" /></p>
                      <h6>{item.name}</h6>
                      <CurrencyFormat value={item.price} displayType={'text'} thousandSeparator={true} suffix={' â‚«'} renderText={value => 
                          <p>{value}</p>} />
                  </Link>

                  <form action="">
                      <input className="star star-5" id="star-5" type="radio" name="star" />
                      <label className="star star-5" htmlFor="star-5"></label>
                      <input className="star star-4" id="star-4" type="radio" name="star" />
                      <label className="star star-4" htmlFor="star-4"></label>
                      <input className="star star-3" id="star-3" type="radio" name="star" />
                      <label className="star star-3" htmlFor="star-3"></label>
                      <input className="star star-2" id="star-2" type="radio" name="star" />
                      <label className="star star-2" htmlFor="star-2"></label>
                      <input className="star star-1" id="star-1" type="radio" name="star" />
                      <label className="star star-1" htmlFor="star-1"></label>
                  </form>
              </div>
          </div>
            })}
        </div>
      </div>
      <div className="footer-ex">
        <Footer />
      </div>
    </div>
  )
}
