import React, { useState, useEffect, useContext } from 'react';
import { useForm } from "react-hook-form";
import axios from 'axios';
import { useParams } from "react-router-dom";
// import { Card, CardMedia, CardContent, CardActions, Typography, IconButton } from '@material-ui/core';
import { AddShoppingCart } from '@material-ui/icons';
import Popup from "reactjs-popup";
import Menu from '../home/Menu';
import "../products/css/Detail.css"
import { Context } from '../../contexts/Context';
import {
    Card,
    CardBody,
    CardTitle,
    CardSubtitle,
    Button,
    Table,
    Modal,
    ModalBody,
    Input,
    FormGroup,
    Label,
} from "reactstrap";
import CurrencyFormat from 'react-currency-format';

const DetailPro = () => {

    const { register, handleSubmit, formState: { errors } } = useForm();
    let { id } = useParams();
    const [data, setData] = useState([]);
    const [qty, setQty] = useState(1)

    const [category, setCategory] = useState([]);
    const [deleteId, setDeleteId] = useState(0);
    const [editId, setEditId] = useState(0);
    const [name, setName] = useState("");
    const [open, setOpen] = useState(false);
    const [openDelete, setOpenDelete] = useState(false);

    const { addCart } = useContext(Context)

    useEffect(() => {
        const fetchData = async () => {
            await axios("http://localhost:8080/detail/" + id)
                .then(result => {
                    setData(result.data);
                },
                    (error) => {
                        console.log(error);
                    }
                )
        };
        fetchData();
    }, [])

    const onSubmit = () => {
        console.log("data")
    }

    const reduction = () => {
        setQty((prevQty) => {
            if (prevQty - 1 < 1) return 1;

            return prevQty - 1;
        });
    }

    const increase = () => {
        setQty((preQty) => preQty + 1)
    }

    const paynow = () => {

    }


    const renderData = (data) => {
        return (
            <>
                {data.map((item, index) => {
                    return (
                        <main key={index} className="container-cart">

                            <div className="left-column">
                                <img src={item.image} alt="" style={{ width: '350px' }} />

                            </div>

                            <div className="right-column">
                                <div className="product-description">
                                    <span>Sản phẩm</span>
                                    <h1>{item.name}</h1>
                                    <p>{item.description}</p>
                                </div>

                                <div className="product-configuration">

                                    <div className="cable-config">
                                        <span>Phiên bản</span>
                                        <div className="cable-choose">
                                            <button>6/128GB</button>
                                            <button>6/256GB</button>
                                            <button>8/512GB</button>
                                        </div>
                                        <div className="product-price">
                                            <CurrencyFormat value={item.price} displayType={'text'} thousandSeparator={true} suffix={'₫'} renderText={value =>
                                                <span className='icon-price'>{value}</span>} />
                                        </div>
                                        <a href="#">Bảo hành 12 tháng tại trung tâm ủy quyền chính hãng</a>
                                    </div>
                                </div>

                                <Popup modal trigger={<button onClick={() => paynow(item.id)} className="cart-btn">Mua ngay</button>}>
                                    <div className="cart-layout">
                                        <div className="cart-items">
                                            <div className="item " data-sku="RN11N">
                                                <div className="img">
                                                    <img src="" alt="" />
                                                    <p className="title">{item.name}</p>
                                                    <p className="price">
                                                        <i className="icon-flash text-red"></i>
                                                        <CurrencyFormat value={item.price} displayType={'text'} thousandSeparator={true} suffix={' ₫'} renderText={value =>
                                                            <strong className='icon-price'>{value}</strong>} />

                                                    </p>
                                                    <div className="number">
                                                        <label>Số lượng</label>
                                                        <div className="control">
                                                            <button onClick={() => reduction(item.id)}> - </button>
                                                            <span>{qty}</span>
                                                            <button onClick={() => increase(item.id)}> + </button>
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className="info">
                                                    <div className="options">
                                                        <div className="option">
                                                            <strong>Phiên bản</strong>
                                                            <label>
                                                                <i className="icon-checked"></i>
                                                                <span>4GB/64GB</span>
                                                            </label>
                                                        </div>

                                                        <div className="option">
                                                            <strong>Màu sắc</strong>
                                                            <label>
                                                                <i className="icon-checked"></i>
                                                                <span>Xanh Nhạt</span>
                                                            </label>
                                                        </div>
                                                    </div>
                                                </div>

                                            </div>
                                        </div>

                                        <div className="cart-total">
                                            <CurrencyFormat value={item.price * qty} displayType={'text'} thousandSeparator={true} suffix={' ₫'} renderText={value =>
                                                <p>Tổng thanh toán:<strong className='icon-price'>{value}</strong></p>} />
                                            {/* <p> <strong className="price text-red"></strong></p> */}
                                            <button className="next">Tiếp tục</button>
                                        </div>
                                        <div className="cart-form">
                                            <form onSubmit={handleSubmit(onSubmit)}>

                                                <input type="hidden" name="OrderPayTypeID" value="1" />

                                                <input type="hidden" name="IsPreOrder" id="IsPreOrder" value="False" />
                                                <input name="__RequestVerificationToken" type="hidden" value="mqG3723N3oa0QrgsD1zqaRtpQsxJU1h0GgWqQ3fNphD3dq5ZElv-S9VUgbUyQ-ZwzWnPDp5mlIbCw7TxjctDCThckJk1" />
                                                <h3>Thông tin đặt hàng</h3>
                                                <p className="text-gray"><i>Bạn cần nhập đầy đủ các trường thông tin có dấu *</i></p>
                                                <div className="row">
                                                    <div className="col">
                                                        <div className="control">
                                                            <input name="Title" type="text" placeholder="Họ và tên *" data-required="1" />
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className="row">
                                                    <div className="col">
                                                        <div className="control">
                                                            <input name="Phone" type="tel" placeholder="Số điện thoại *" data-required="1" />
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className="row">
                                                    <div className="col">
                                                        <div className="control">
                                                        </div>
                                                    </div>
                                                </div>
                                                <div className="row shInfo">
                                                    <div className="col">
                                                        <div className="control">
                                                            <input name="Address" type="text" placeholder="Địa chỉ nhận hàng *" data-required="1" />
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className="row shInfo">
                                                    <div className="col">
                                                        <div className="control">
                                                            <input name="Email" type="email" placeholder="Email" />
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className="row shInfo">
                                                    <div className="col">
                                                        <div className="control">
                                                            <textarea name="Note" placeholder="Ghi chú" data-minlength="15"></textarea>
                                                        </div>
                                                    </div>
                                                </div>
                                            </form>
                                        </div>
                                    </div>

                                </Popup>
                                <Modal isOpen={open} toggle={() => setOpen(false)}>
                                    <ModalBody>
                                        <FormGroup>
                                            <Label for="categoryName">Name</Label>
                                            <Input
                                                id="categoryName"
                                                name="name"
                                                placeholder="Category Name"
                                                type="name"
                                                onChange={(e) => {
                                                    setName(e.target.value);
                                                }}
                                                value={name}
                                            />
                                        </FormGroup>
                                        <Button onClick={createProduct} className="btn" color="primary">
                                            Thêm Danh Mục
                                        </Button>
                                    </ModalBody>
                                </Modal>
                                <Modal isOpen={openDelete} toggle={() => setOpenDelete(false)}>
                                    <ModalBody>
                                        <p>Delete Category?</p>
                                        <Button onClick={deleteCategory} className="btn" color="danger">
                                            Xóa
                                        </Button>
                                    </ModalBody>
                                </Modal>

                                <button onClick={() => addCart(item.id)}>
                                    <AddShoppingCart />
                                </button>

                            </div>
                        </main >
                    );
                })}
            </>
        )
    }

    function NavNoti() {
        var items = [];
        for (var i = 0; i < data.length; i++) {
            items.push(
                <ol key={i} className="noti-header">
                    <li className="noti-item">
                        <a href="/">Trang chủ</a></li>
                    <span> » </span>
                    <li className="noti-item">
                        <a href="/product/1">Điện thoại</a></li>
                    <span> » </span>
                    <li key={i} className="noti-item">
                        <a >{data[i].name}</a></li>
                </ol>
            )
        }
        return (
            <div className="noti">
                {items}
            </div>
        )
    }

    return (
        <>
            <Menu />
            <NavNoti />
            {renderData(data)}
        </>
    );

}

export default DetailPro;
