import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import { useForm } from "react-hook-form";
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Menu from "../home/Menu";
import "../css/Login.css"

function Edit() {
    
    let { id } = useParams();
    console.log(id)
    
    const [data, setData] = useState([]);
    useEffect(() => {
        fetch("http://localhost:8080/editProducts/" + id)
            .then(res => res.json())
            .then(
                (result) => {

                    setData(result);
                    console.log(data);
                    //console.log(result);
                },
                (error) => {
                    console.log("error", error);
                }
            )
    }, [])
    
    
    return (
        <div className="container" id="container">
            <div className="form-container sign-in-container">
                <form onSubmit={handleSubmit(onSubmit)}>
                    <h1>Thêm sản phẩm</h1>

                    <label htmlFor="name"></label>
                    <input type="text" name="name" className="form-control" placeholder="Nhập tên"{...register("name", { required: true, maxLength: 20 })} />
                    <h style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.name && "Tên không để trống và quá 20 kí tự!"}</h>

                    <label htmlFor="price"></label>
                    <input type="number" className="form-control" placeholder="Nhập giá" {...register("price", { required: true, maxLength: 20 })} />
                    <h style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.price && "Giá không để trống và quá 20 kí tự!"}</h>

                    <label htmlFor="image"></label>
                    <input type="text" className="form-control" placeholder="Link ảnh" id="Role" {...register("image")} />
                    <h style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.image && "Không hợp lệ!"}</h>

                    <label htmlFor="idcategory"></label>
                    <select {...register("idcategory")}>
                        <option >Iphone</option>
                        <option >Samsung</option>
                        <option >Realme</option>
                        <option >Oppo</option>
                    </select>
                    

                    <label htmlFor="desciption"></label>
                    <input type="text" className="form-control" placeholder="Mô tả" id="Role" {...register("desciption")} />

                    <button type="submit">Sửa</button>
                </form>
            </div>

            <div className="overlay-container">
                <div className="overlay">
                    <div className="overlay-panel overlay-left">
                        <h1>Welcome Back!</h1>
                        <p>To keep connected with us please login with your personal info</p>
                        <button className="ghost" id="signIn">Sign In</button>
                    </div>
                    <div className="overlay-panel overlay-right">
                        <h1>Hello, Friend!</h1>
                        <p>Enter your personal details and start journey with us</p>
                        <Link to="/phonepage/1">
                            <button className="ghost" id="signUp">Sign Up</button>
                        </Link>

                    </div>
                </div>
            </div>
        </div>
    )
}

export default Edit;
