import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import Menu from '../home/Menu';
import "../css/Detail.css"

function DetailPro() {
    let { id } = useParams();
    console.log(id)

    const [data, setData] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            await axios("http://localhost:8080/detail/" + id)
                .then(result => {
                    console.log(result);
                    setData(result.data);
                },
                    (error) => {
                        console.log(error);
                    }
                )
        };
        fetchData();
    }, [])

    const productDetails = (data) => {
        return (
            <main>
                {data.map((item, index) => {
                    return (
                        <main key={index} className="container-cart">

                            <div className="left-column">
                                <img src={item.image} alt="" style={{ width: '350px' }} />

                            </div>

                            <div className="right-column">
                                <div className="product-description">
                                    <span>Sản phẩm</span>
                                    <h1>{item.name}</h1>
                                    <p>{item.description}</p>
                                </div>

                                <div className="product-configuration">
                                    {/* <div className="product-color">
                        <span>Màu</span>
                        <div className="color-choose">
                            <div>
                                <input data-image="red" type="radio" id="red" name="color" value="red" />
                                <label htmlFor="red"><span></span></label>
                            </div>
                            <div>
                                <input data-image="blue" type="radio" id="blue" name="color" value="blue" />
                                <label htmlFor="blue"><span></span></label>
                            </div>
                            <div>
                                <input data-image="black" type="radio" id="black" name="color" value="black" />
                                <label htmlFor="black"><span></span></label>
                            </div>
                        </div>
                    </div> */}

                                    <div className="cable-config">
                                        <span>Phiên bản</span>
                                        <div className="cable-choose">
                                            <button>6/128GB</button>
                                            <button>6/256GB</button>
                                            <button>8/512GB</button>
                                        </div>
                                        <div className="product-price">
                                            <span className='icon-price'>{item.price} ₫</span>
                                        </div>
                                        <a href="#">Bảo hành 12 tháng tại trung tâm ủy quyền chính hãng</a>
                                    </div>
                                </div>

                                <button onClick={(e) => addCart(item.id, item.name, item.price, item.image, 1)} className="cart-btn">Add to cart</button>
                            </div>
                        </main >
                    );
                })
                }
            </main>
        )
    }


    function NavNoti() {
        var items = [];
        for (var i = 0; i < data.length; i++) {
            items.push(
                <ol key={i} className="noti-header">
                    <li className="noti-item">
                        <a href="/">Trang chủ</a></li>
                    <span> » </span>
                    <li className="noti-item">
                        <a href="/product/1">Điện thoại</a></li>
                    <span> » </span>
                    <li key={i} className="noti-item">
                        <a >{data[i].name}</a></li>
                </ol>
            )
        }
        return (
            <div className="noti">
                {items}
            </div>
        )
    }

    function addCart(id, productName, price, images, quantity) {
        console.log(data)
        var arrProduct = [];
        var listItems = new Object();
        listItems["id"] = id;
        listItems["productName"] = productName;
        listItems["price"] = price;
        listItems["images"] = images;
        listItems["quantity"] = quantity;

        if (localStorage.getItem("cart") == null) {
            arrProduct.push(listItems);
            localStorage.setItem("cart", JSON.stringify(arrProduct))
        } else {
            var getListItems = JSON.parse(localStorage.getItem("cart"));

            console.log(getListItems);
            for (let i = 0; i < getListItems.length; i++) {
                console.log(getListItems[i].id)
                if (getListItems[i].id != id) {
                    getListItems.push(listItems);
                    localStorage.setItem("cart", JSON.stringify(getListItems))
                } else {
                    const ssq = getListItems[i].quantity;
                    getListItems[i].quantity == ssq + quantity
                }
            }

        }

    }

    return (
        <>
            <Menu />
            <NavNoti />
            {productDetails(data)}


        </>
    );

}

export default DetailPro;
