import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import { useForm } from "react-hook-form";
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Menu from "../home/Menu";
import "../css/Login.css"

function Edit() {
    const { register, handleSubmit, formState: { errors } } = useForm();
    const [ name, setName ] = useState('');
    const [ image, setImage ] = useState('');
    const [ price, setPrice ] = useState('');
    const [ category, setCategory ] = useState('');
    const [ desciption, setDesciption ] = useState('');
    
    let params = useParams();
    console.log(params)
    
    const [data, setData] = useState([]);
    const onSubmit = data => {
        console.log(data)
        var config = {
            method: "put",
            url: "http://localhost:8080/editProduct/" + params.id,
            data: data
        };
        console.log(config)
        axios(config)
            .then(res => res.json())
            .then(
                (result) => {

                    setData(result);
                    console.log(data);
                    //console.log(result);
                },
                (error) => {
                    console.log(error);
                }
            )
    }
    
    useEffect(() => {
        getProductDetails();
    }, [])

    const getProductDetails = async () => {
        let result = await fetch(`http://localhost:8080/editProduct/${params.id}`);
        result = await result.json();
        console.log(result);
        setName(result.name)
    }
    
    return (
        <div className="container" id="container">
            <div className="form-container sign-in-container">
                <form onSubmit={handleSubmit(onSubmit)}>
                    <h1>Sửa sản phẩm</h1>

                    <label htmlFor="name"></label>
                    <input type="text" name="name" className="form-control" value={name} onChange={(e) => {setName(e.target.value)}} placeholder="Nhập tên" />
                    

                    <label htmlFor="price"></label>
                    <input type="number" className="form-control" placeholder="Nhập giá" {...register("price", { required: true, maxLength: 20 })} />
                    

                    <label htmlFor="image"></label>
                    <input type="text" className="form-control" placeholder="Link ảnh" id="Role" {...register("image")} />
                   

                    <label htmlFor="idcategory"></label>
                    <select {...register("idcategory")}>
                        <option >1</option>
                        <option >2</option>
                        <option >3</option>
                    </select>
                    

                    <label htmlFor="desciption"></label>
                    <input type="text" className="form-control" placeholder="Mô tả" id="Role" {...register("desciption")} />

                    <button type="submit">Sửa</button>
                </form>
            </div>

            <div className="overlay-container">
                <div className="overlay">
                    <div className="overlay-panel overlay-left">
                        <h1>Welcome Back!</h1>
                        <p>To keep connected with us please login with your personal info</p>
                        <button className="ghost" id="signIn">Sign In</button>
                    </div>
                    <div className="overlay-panel overlay-right">
                        <h1>Hello, Friend!</h1>
                        <p>Enter your personal details and start journey with us</p>
                        <Link to="/phonepage/1">
                            <button className="ghost" id="signUp">Sign Up</button>
                        </Link>

                    </div>
                </div>
            </div>
        </div>
    )
}

export default Edit;
