import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import Menu from "../home/Menu";
import "../dashboard/css/Admin.css";
function Admin() {

    const [data, setData] = useState([]);
    const [tongtrang, setTongtrang] = useState(0);
    var items = [];
    let { numberpage } = useParams();
    useEffect(() => {
        const fetchData = async () => {
            await axios("http://localhost:8080/admin/products/" + numberpage)
                .then(result => {
                    console.log(result);
                    setData(result.data);
                },
                    (error) => {
                        console.log(error);
                    }
                )
        };
        fetchData();

        /*tonguser*/

        fetch("http://localhost:8080/countproducts")
            .then(res => res.json())
            .then(
                (result) => {

                    setTongtrang(Math.ceil(result[0].total / 10));

                },
                (error) => {
                    console.log(error);
                }
            )
    }, [])


    function Pagination() {
        console.log(tongtrang);
        var items = []

        for (var i = 1; i <= tongtrang; i++) {
            items.push(
                <li key={i} className="page-item"><a className="page-link" href={'/admin/products/' + i}>{i}</a></li>

            );
        }
        return (
            <nav aria-label="Page navigation example">
                <ul className="pagination">
                    {items}
                </ul>
            </nav>
        )

    }

    function DeleteProduct(id, e) {
        console.log(id)

        if (window.confirm("Bạn có muốn xóa?")) {
            fetch("http://localhost:8080/delete/" + id)
                .then(res => res.json())
                .then(
                    (result) => {
                        window.location.reload(true);
                    },
                    (error) => {
                        console.log("error", error);
                    }
                )
        }

    }

    return (


        <div>
            <Card>
                <CardBody>
                    <CardTitle tag="h5">Danh Sách Sản Phẩm</CardTitle>
                    <div className="d-flex">
                        <CardSubtitle className="mb-2 text-muted me-auto" tag="h6">
                            Tất cả sản phẩm có trong cửa hàng
                        </CardSubtitle>

                        <Button
                            onClick={() => setOpen(true)}
                            className="btn"
                            color="primary"
                        >
                            {/* <GrAdd /> */}
                            Thêm Danh Mục
                        </Button>
                    </div>
                    <Table className="no-wrap mt-3 align-middle" responsive borderless>
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Ảnh</th>
                                <th scope="col">Tên sản phẩm</th>
                                <th scope="col">Giá</th>
                                <th scope="col">Loại</th>
                                <th scope="col">Mô tả</th>
                                <th scope="col">Thao tác</th>

                            </tr>
                        </thead>
                        <tbody>
                            {data.map((item, index) => {
                                return (
                                    <tr key={index} className="border-top">
                                        <th scope="row">{item.id}</th>
                                        <td>
                                            <img
                                                src={item.image}
                                                style={{ width: "100px", height: "100px" }}
                                            ></img>
                                        </td>
                                        <td>{item.name}</td>
                                        <td>{item.price}</td>
                                        <td>{item.category}</td>
                                        <td className="description">{item.description}</td>
                                        <td>
                                            <button
                                                onClick={() => {
                                                    setOpen(true);
                                                    setEditId(item.id);
                                                    setName(item.name);
                                                }}
                                                type="button"
                                                class="btn btn-success"
                                            >
                                                {/* <GrEdit /> */}
                                            </button>
                                            <button className="button-one">
                                                <Link to={`/editProduct/${item.id}`}>
                                                    <i className="ic1 fas fa-edit">edit</i> </Link>
                                            </button>

                                            <button
                                            onClick={() => {
                                                setOpenDelete(true);
                                                setDeleteId(item.id);
                                            }}
                                            type="button"
                                            class="btn btn-danger"
                                        >
                                            {/* <BsTrash /> */}
                                        </button>
                                            <button className="button-one" onClick={(e) => DeleteProduct(item.id)}>
                                                <span>
                                                    <i className="ic1 far fa-trash-alt"></i>
                                                </span>
                                            </button>
                                        </td>
                                    </tr>
                                );
                            })}

                        </tbody>
                        <tbody>
                            {category.map((item, index) => (
                                <tr key={index} >
                                    <td>{item.id}</td>
                                    <td>{item.name}</td>
                                    <td>
                                        <button
                                            onClick={() => {
                                                setOpen(true);
                                                setEditId(item.id);
                                                setName(item.name);
                                            }}
                                            type="button"
                                            class="btn btn-success"
                                        >
                                            {/* <GrEdit /> */}
                                        </button>
                                        <button
                                            onClick={() => {
                                                setOpenDelete(true);
                                                setDeleteId(item.id);
                                            }}
                                            type="button"
                                            class="btn btn-danger"
                                        >
                                            {/* <BsTrash /> */}
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </CardBody>
            </Card>
            <Modal isOpen={open} toggle={() => setOpen(false)}>
                <ModalBody>
                    <FormGroup>
                        <Label for="categoryName">Name</Label>
                        <Input
                            id="categoryName"
                            name="name"
                            placeholder="Category Name"
                            type="name"
                            onChange={(e) => {
                                setName(e.target.value);
                            }}
                            value={name}
                        />
                    </FormGroup>
                    <Button onClick={onSubmit} className="btn" color="primary">
                        Thêm Danh Mục
                    </Button>
                </ModalBody>
            </Modal>
            {/* <Modal isOpen={openDelete} toggle={() => setOpenDelete(false)}>
                <ModalBody>
                    <p>Delete Category?</p>
                    <Button onClick={deleteCategory} className="btn" color="danger">
                        Xóa
                    </Button>
                </ModalBody>
            </Modal> */}
            <div className="btnAdd">
                <Link to={'/add'}>Add</Link>
            </div>
            <table className="table">

            </table>
            <Pagination />
        </div>
    );
}

export default Admin;
