import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import { useForm } from "react-hook-form";
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Menu from "../home/Menu";
import "../css/Login.css"

function Edit() {
    const { register, handleSubmit, formState: { errors } } = useForm();

    let { id } = useParams();
    console.log(id)

    const [data, setData] = useState([]);
    const onSubmit = data => {
        console.log(data);
        var config = {
            method: "put",
            url: "http://localhost:8080/editproduct/" + id,
            data: data
        };
        console.log(data)
        axios(config)
            .then(function (response) {
                window.location.assign('http://localhost:3000/admin/products/1');
            })
            .then(
                (result) => {

                    setData(result);
                    console.log(data);
                    //console.log(result);
                },
                (error) => {
                    console.log("error", error);
                }
            )
    }



    return (
        <><Menu />
            <div className="container" id="container">

                <div className="form-container sign-in-container">
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <h1>Sửa sản phẩm</h1>

                        <label htmlFor="name"></label>
                        <input type="text" name="name" className="form-control" placeholder="Nhập tên"{...register("name", { required: true, maxLength: 20 })} />
                        <p style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.name && "Tên không để trống và quá 20 kí tự!"}</p>

                        <label htmlFor="price"></label>
                        <input type="number" className="form-control" placeholder="Nhập giá" {...register("price", { required: true, maxLength: 20 })} />
                        <p style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.price && "Giá không để trống và quá 20 kí tự!"}</p>

                        <label htmlFor="image"></label>
                        <input type="text" className="form-control" placeholder="Link ảnh" id="Role" {...register("image")} />
                        <p style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.image && "Không hợp lệ!"}</p>

                        <label htmlFor="category"></label>
                        <select {...register("category")}>
                            <option >Iphone</option>
                            <option >Samsung</option>
                            <option >Realme</option>
                            <option >Oppo</option>
                        </select>


                        <label htmlFor="description"></label>
                        <input type="text" className="form-control" placeholder="Mô tả" id="Role" {...register("description")} />

                        <button type="submit">Sửa</button>
                    </form>
                </div>

            </div>
        </>
    )
}

export default Edit;
