import "./App.css";
import "./css/Login.css";

import React, { useState, useEffect } from 'react';
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import NotFound from "./components/NotFound";
// import DetailPro from "./DetailPro.js";
import Cart from "./components/Cart";
import Login from "./components/Login";
import Admin from "./components/Admin";
import Menu from "./home/Menu";
import Slider from "./home/Slider";
import Footer from "./home/Footer";
import ListProduct from "./components/ListProduct";
import DetailPro from "./components/DetailPro";
import Home from "./components/Home";
import Add from "./components/Add";
import Edit from "./components/Edit";


function App() {

    return (

        <Routes>
            <Route exact path="/home" element={<Home />} />
            <Route path="/detailPro/:id" element={<DetailPro />} />
            <Route path="/products/:numberpage" element={<ListProduct />} />
            <Route path="/cart" element={<Cart />} />
            <Route path="/login" element={<Login />} />
            <Route path="/admin/products/:numberpage" element={<Admin />} />
            <Route path="/add" element={<Add />} />
            <Route path="/editProduct/:id" element={<Edit />}></Route>
            <Route path="/*" element={<NotFound />} />

        </Routes>
    );
}


export default App;
