import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import React, { useState, useEffect } from 'react';
import {
    Card,
    CardBody,
    CardTitle,
    CardSubtitle,
    Button,
    Table,
    Modal,
    ModalBody,
    Input,
    FormGroup,
    Label,
} from "reactstrap";
import axios from 'axios';
import "../dashboard/css/Add.css"

function Add() {
    const { register, handleSubmit, formState: { errors } } = useForm();
    const [category, setCategory] = useState([]);
    const [deleteId, setDeleteId] = useState(0);
    const [editId, setEditId] = useState(0);
    const [name, setName] = useState("");
    const [open, setOpen] = useState(false);
    const [openDelete, setOpenDelete] = useState(false);


    const onSubmit = (data) => {
        console.log(data);
        var config = {
            method: "post",
            url: "http://localhost:8080/api/product",
            data: data
        };
        axios(config)
            .then(function (response) {
                console.log(response);

                window.location.assign('http://localhost:3000/admin/products/1');
                if (response.data == "Thành Công") {

                }


            })
            .catch(function (error) {
                console.log(error);
            });
        console.log(data)
    }
    return (
        
        <div className="container" id="container">
            <div className="form-container sign-in-container">
                <form onSubmit={handleSubmit(onSubmit)}>
                    <h1>Thêm sản phẩm</h1>

                    <label htmlFor="name"></label>
                    <input type="text" name="name" className="form-control" placeholder="Nhập tên"{...register("name", { required: true, maxLength: 20 })} />
                    <h style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.name && "Tên không để trống và quá 20 kí tự!"}</h>

                    <label htmlFor="price"></label>
                    <input type="number" className="form-control" placeholder="Nhập giá" {...register("price", { required: true, maxLength: 20 })} />
                    <h style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.price && "Giá không hợp lệ!"}</h>

                    <label htmlFor="image"></label>
                    <input type="text" className="form-control" placeholder="Link ảnh" id="Role" {...register("image")} />
                    <h style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.image && "Không hợp lệ!"}</h>

                    <label htmlFor="idcategory"></label>
                    <select {...register("idcategory")}>
                        <option >iPhone</option>
                        <option >SamSung</option>
                        <option >Realme</option>
                        <option >OPPO</option>
                    </select>


                    <label htmlFor="description"></label>
                    <input type="text" className="form-control" placeholder="Mô tả" id="Role" {...register("description")} />

                    <button type="submit" className="btn btn-primary">Thêm</button>
                </form>
            </div>
        </div>
    )
}

export default Add;
