import axios from 'axios';
import React, { createContext, useState, useEffect } from 'react'

export const Context = createContext();

export const DataProvider = ({ children }) => {
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState([]);
    const [total, setTotal] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            await axios("http://localhost:8080")
                .then(result => {
                    setProducts(result.data);

                },
                    (error) => {
                        console.log(error);
                    }
                )
        }
        fetchData()
    }, [])

    useEffect(() => {
        const dataCart = JSON.parse(localStorage.getItem('dataCart'));
        if (dataCart !== null) {
            setCart(dataCart);
        }
        const dataTotal = JSON.parse(localStorage.getItem('dataTotal'));
        if (dataTotal !== null) {
            setTotal(dataTotal);
        }
    }, [])

    const addCart = (id) => {
        console.log(products)
        const check = cart.every(item => {
            return item.id !== id;
        })
        if (check) {
            const data = products.filter(product => {
                return product.id === id;
            })
            setCart([...cart, ...data])
        } else {
            window.confirm("Thêm thành công!")
            increase(id)
        }
    }

    const reduction = id => {
        cart.forEach(item => {
            if (item.id === id) {
                // if(item.count <= 1) {
                //     removeProduct(id)
                // }else {
                //     item.count -= 1
                // }
                item.count <= 1 ? removeProduct(id) : item.count -= 1;

            }
        })
        setCart(cart)
        getTotal()
    }

    const increase = id => {
        cart.forEach(item => {
            if (item.id === id) {
                item.count += 1;
            }
        })
        
        setCart(cart)
        getTotal()
    }

    const removeProduct = id => {
        if (window.confirm('Are you sure you want to remove Product?')) {
            cart.forEach((item, index) => {
                if (item.id === id) {
                    cart.splice(index, 1);
                }
            })
            setCart(cart)
            getTotal()
        }
    }

    const getTotal = () => {
        const res = cart.reduce((prev, item) => {
            return prev + (item.count * item.price);
        }, 0)
        setTotal(res)
    }

    useEffect(() => {
        localStorage.setItem('dataCart', JSON.stringify(cart));
        localStorage.setItem('dataTotal', JSON.stringify(total));
    })

    const value = { products, addCart, cart, reduction, increase, removeProduct, total, getTotal };
    return (
        <Context.Provider value={value}> {children} </Context.Provider>
    )

}
