import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import {
    useParams
} from "react-router-dom";
// import { Card, CardMedia, CardContent, CardActions, Typography, IconButton } from '@material-ui/core';
import { AddShoppingCart } from '@material-ui/icons';
import Popup from "reactjs-popup";
import Menu from '../home/Menu';
import "../products/css/Detail.css"
import { Context } from '../../contexts/Context';
import CurrencyFormat from 'react-currency-format';

const DetailPro = () => {

    let { id } = useParams();
    const [data, setData] = useState([]);
    const { addCart } = useContext(Context)

    console.log(data)
    useEffect(() => {
        const fetchData = async () => {
            await axios("http://localhost:8080/detail/" + id)
                .then(result => {
                    setData(result.data);
                },
                    (error) => {
                        console.log(error);
                    }
                )
        };
        fetchData();
    }, [])


    const renderData = (data) => {
        return (
            <main>
                {data.map((item, index) => {
                    return (
                        <main key={index} className="container-cart">

                            <div className="left-column">
                                <img src={item.image} alt="" style={{ width: '350px' }} />

                            </div>

                            <div className="right-column">
                                <div className="product-description">
                                    <span>Sản phẩm</span>
                                    <h1>{item.name}</h1>
                                    <p>{item.description}</p>
                                </div>

                                <div className="product-configuration">

                                    <div className="cable-config">
                                        <span>Phiên bản</span>
                                        <div className="cable-choose">
                                            <button>6/128GB</button>
                                            <button>6/256GB</button>
                                            <button>8/512GB</button>
                                        </div>
                                        <div className="product-price">
                                            <CurrencyFormat value={item.price} displayType={'text'} thousandSeparator={true} suffix={'₫'} renderText={value =>
                                                <span className='icon-price'>{value}</span>} />
                                        </div>
                                        <a href="#">Bảo hành 12 tháng tại trung tâm ủy quyền chính hãng</a>
                                    </div>
                                </div>

                                <Popup modal trigger={<button className="cart-btn">Mua ngay</button>}>
                                    <div class="cart-form">
                                        <form onsubmit="return validFormOrder(this);">

                                            <input type="hidden" name="OrderPayTypeID" value="1" />

                                            <input type="hidden" name="IsPreOrder" id="IsPreOrder" value="False" />
                                            <input name="__RequestVerificationToken" type="hidden" value="mqG3723N3oa0QrgsD1zqaRtpQsxJU1h0GgWqQ3fNphD3dq5ZElv-S9VUgbUyQ-ZwzWnPDp5mlIbCw7TxjctDCThckJk1" />
                                            <h3>Thông tin đặt hàng</h3>
                                            <p class="text-gray"><i>Bạn cần nhập đầy đủ các trường thông tin có dấu *</i></p>
                                            <div class="row">
                                                <div class="col">
                                                    <div class="control">
                                                        <input name="Title" type="text" placeholder="Họ và tên *" data-required="1" />
                                                    </div>
                                                </div>
                                            </div>

                                            <div class="row">
                                                <div class="col">
                                                    <div class="control">
                                                        <input name="Phone" type="tel" placeholder="Số điện thoại *" data-required="1" />
                                                    </div>
                                                </div>
                                            </div>

                                            <div class="row">
                                                <div class="col">
                                                    <div class="control">
                                                    </div>
                                                </div>
                                            </div>
                                            <div class="row shInfo">
                                                <div class="col">
                                                    <div class="control">
                                                        <input name="Address" type="text" placeholder="Địa chỉ nhận hàng *" data-required="1" />
                                                    </div>
                                                </div>
                                            </div>

                                            <div class="row shInfo">
                                                <div class="col">
                                                    <div class="control">
                                                        <input name="Email" type="email" placeholder="Email" />
                                                    </div>
                                                </div>
                                            </div>

                                            <div class="row shInfo">
                                                <div class="col">
                                                    <div class="control">
                                                        <textarea name="Note" placeholder="Ghi chú" spellcheck="false" data-minlength="15"></textarea>
                                                    </div>
                                                </div>
                                            </div>

                                        </form>
                                    </div>

                                </Popup>

                                <button onClick={() => addCart(item.id)}>
                                    <AddShoppingCart />
                                </button>
                            </div>
                        </main >
                    );
                })
                }
            </main>
        )
    }

    function NavNoti() {
        var items = [];
        for (var i = 0; i < data.length; i++) {
            items.push(
                <ol key={i} className="noti-header">
                    <li className="noti-item">
                        <a href="/">Trang chủ</a></li>
                    <span> » </span>
                    <li className="noti-item">
                        <a href="/product/1">Điện thoại</a></li>
                    <span> » </span>
                    <li key={i} className="noti-item">
                        <a >{data[i].name}</a></li>
                </ol>
            )
        }
        return (
            <div className="noti">
                {items}
            </div>
        )
    }

    return (
        <>
            <Menu />
            <NavNoti />
            {renderData(data)}
        </>
    );

}

export default DetailPro;
