import React, { createContext, useState, useEffect, Component } from 'react';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import { Card, CardMedia, CardContent, CardActions, Typography, IconButton } from '@material-ui/core';
import { AddShoppingCart } from '@material-ui/icons';
import Popup from "reactjs-popup";
import Menu from '../home/Menu';
import "../products/css/Detail.css"
import { useContext } from 'react';

import { DataContext } from '../../contexts/Context';

function DetailPro() {

    const contextType = DataContext;

    let { id } = useParams();

    const [data, setData] = useState([]);
    // const [cartItems, setCartItems] = useState([]);
    // const [cartCount, setCartCount] = useState(0)
    

    // const handleAddToCart = () => addItemToCart(data);

    // const addCartItem = (cartItems, data) => {
    //     console.log(cartItems, "-" ,data)
    //     const checkCartItems = cartItems.find(
    //         (cartItem) => cartItem.id === data.id
    //     );
    //     console.log(checkCartItems)
    //     if (checkCartItems) {
    //         return cartItems.map(
    //             (cartItem) => cartItem.id === data.id ?
    //             {...cartItem, quantity: cartItem.quantity +1} : cartItem
                
    //         )
    //     }
        
    //     return [...cartItems, {...data, quantity: 1}]
    // };

    // useEffect(() => {
    //     const newCartCount = cartItems.reduce((total, currentItem) => total + currentItem.quantity, 0)
    //     setCartCount(newCartCount);
        
        
    // }, [cartItems]);
    // console.log(cartItems);
    

    // const addItemToCart = (data) => {
    //     setCartItems(addCartItem(cartItems, data));
           
    // };

    useEffect(() => {
        const fetchData = async () => {
            await axios("http://localhost:8080/detail/" + id)
                .then(result => {
                    setData(result.data);
                },
                    (error) => {
                        console.log(error);
                    }
                )
        };
        fetchData();
    }, [])

    const renderData = (data) => {
        const {addCart} = DataContext;
        return (
            <main>
                {data.map((item, index) => {
                    return (
                        <main key={index} className="container-cart">

                            <div className="left-column">
                                <img src={item.image} alt="" style={{ width: '350px' }} />

                            </div>

                            <div className="right-column">
                                <div className="product-description">
                                    <span>Sản phẩm</span>
                                    <h1>{item.name}</h1>
                                    <p>{item.description}</p>
                                </div>

                                <div className="product-configuration">

                                    <div className="cable-config">
                                        <span>Phiên bản</span>
                                        <div className="cable-choose">
                                            <button>6/128GB</button>
                                            <button>6/256GB</button>
                                            <button>8/512GB</button>
                                        </div>
                                        <div className="product-price">
                                            <span className='icon-price'>{item.price} ₫</span>
                                        </div>
                                        <a href="#">Bảo hành 12 tháng tại trung tâm ủy quyền chính hãng</a>
                                    </div>
                                </div>
                                <Popup modal trigger={<button className="cart-btn">Mua ngay</button>}>
                                    Modal Content
                                </Popup>
                                
                                <IconButton aria-label="Add to Cart" onClick={() => addCart(item.id)}>
                                    <AddShoppingCart />
                                </IconButton>
                            </div>
                        </main >
                    );
                })
                }
            </main>
        )
    }

    function NavNoti() {
        var items = [];
        for (var i = 0; i < data.length; i++) {
            items.push(
                <ol key={i} className="noti-header">
                    <li className="noti-item">
                        <a href="/">Trang chủ</a></li>
                    <span> » </span>
                    <li className="noti-item">
                        <a href="/product/1">Điện thoại</a></li>
                    <span> » </span>
                    <li key={i} className="noti-item">
                        <a >{data[i].name}</a></li>
                </ol>
            )
        }
        return (
            <div className="noti">
                {items}
            </div>
        )
    }

    return (
        <>
            <Menu />
            <NavNoti />
            {renderData(data)}
        </>
    );

}

export default DetailPro;
