import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import Menu from "../home/Menu";
import "../css/Admin.css";
import Edit from "./Edit";
function Admin() {

    const [data, setData] = useState([]);
    const [tongtrang, setTongtrang] = useState(0);
    var items = [];
    let { numberpage } = useParams();
    useEffect(() => {
        const fetchData  = async () => {
            const result = await axios("http://localhost:8080/admin/products/" + numberpage);
            setData(result.data);
        };
        fetchData();
        
        /*tonguser*/
        
        fetch("http://localhost:8080/countproducts")
            .then(res => res.json())
            .then(
                (result) => {

                    setTongtrang(Math.ceil(result[0].total / 10));

                },
                (error) => {
                    console.log("error");
                }
            )
    }, [])


    function Pagination() {
        console.log(tongtrang);
        var items = []

        for (var i = 1; i <= tongtrang; i++) {
            items.push(
                <li key={i} className="page-item"><a className="page-link" href={'/admin/products/' + i}>{i}</a></li>

            );
        }
        return (
            <nav aria-label="Page navigation example">
                <ul className="pagination">
                    {items}
                </ul>
            </nav>
        )

    }


    const renderData = (data) => {
        return (
            <tbody>
                {data.map((item, index) => {
                    return (
                        <tr key={index}>
                            <th scope="row">{item.id}</th>
                            <td>
                                <img
                                    src={item.image}
                                    style={{ width: "100px", height: "100px" }}
                                ></img>
                            </td>
                            <td>{item.name}</td>
                            <td>{item.price}</td>
                            <td>{item.category}</td>
                            <td className="description">{item.description}</td>
                            <td>
                                <button className="button-one">
                                    <Link to={`/editProduct/${item.id}`}>
                                    <i className="ic1 fas fa-edit">edit</i> </Link>
                                </button>
                                <button className="button-one" onClick={(e) => DeleteProduct(item.id)}>
                                    <span>
                                        <i className="ic1 far fa-trash-alt"></i>
                                    </span>
                                </button>
                            </td>
                        </tr>
                    );
                })}

            </tbody>
        );
    };

    const UpdateProduct = (id, data) => {

    }
    // const renderItem = () => {
    //     let { items, idEdit, name, image, price, idcategory, description } = this.state;
    //     if (items.length === 0) {
    //         return <Item item={0} />
    //     }
    //     return mapld(items, (item, index) => {
    //         if (item.id === idEdit) {
    //             return (
    //                 <Edit
    //                     name={name}
    //                     image={image}
    //                     price={price}
    //                     idcategory={idcategory}
    //                     description={description}
    //                 />
    //             )
    //         }
    //         return (
    //             <a
    //                 key={index}
    //             />
    //         )
    //     });
    // }
    const handleEditItem = (index, item) => {
        console.log(index, item);
        this.setState({
            name: item.name,
            image: item.image,
            price: item.price,
            idcategory: item.idcategory,
            description: item.description,
        });
    }

      

    function DeleteProduct(id, e) {
        console.log(id)

        if (window.confirm("Bạn có muốn xóa?")) {
            fetch("http://localhost:8080/delete/" + id)
                .then(res => res.json())
                .then(
                    (result) => {
                        window.location.reload(true);
                    },
                    (error) => {
                        console.log("error", error);
                    }
                )
        }

    }

    return (


        <div>
            <Menu />
            <div className="btnAdd">
                <a href="/Add">Thêm</a>
            </div>
            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">Ảnh</th>
                        <th scope="col">Tên sản phẩm</th>
                        <th scope="col">Giá</th>
                        <th scope="col">Loại</th>
                        <th scope="col">Mô tả</th>
                        <th scope="col">Thao tác</th>

                    </tr>
                </thead>
                {renderData(data)}
            </table>
            <Pagination />
        </div>
    );
}

export default Admin;
