import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import { Card, CardMedia, CardContent, CardActions, Typography, IconButton } from '@material-ui/core';
import { AddShoppingCart } from '@material-ui/icons';
import Popup from "reactjs-popup";
import Menu from '../home/Menu';
import "../css/Detail.css"

function DetailPro() {
    let { id } = useParams();
    console.log(id)

    const [data, setData] = useState([]);
    const [cart, setCart] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            await axios("http://localhost:8080/detail/" + id)
                .then(result => {
                    console.log(result);
                    setData(result.data);
                },
                    (error) => {
                        console.log(error);
                    }
                )
        };
        fetchData();
    }, [])

    const renderData = (data) => {
        return (
            <main>
                {data.map((item, index) => {
                    return (
                        <main key={index} className="container-cart">

                            <div className="left-column">
                                <img src={item.image} alt="" style={{ width: '350px' }} />

                            </div>

                            <div className="right-column">
                                <div className="product-description">
                                    <span>Sản phẩm</span>
                                    <h1>{item.name}</h1>
                                    <p>{item.description}</p>
                                </div>

                                <div className="product-configuration">

                                    <div className="cable-config">
                                        <span>Phiên bản</span>
                                        <div className="cable-choose">
                                            <button>6/128GB</button>
                                            <button>6/256GB</button>
                                            <button>8/512GB</button>
                                        </div>
                                        <div className="product-price">
                                            <span className='icon-price'>{item.price} ₫</span>
                                        </div>
                                        <a href="#">Bảo hành 12 tháng tại trung tâm ủy quyền chính hãng</a>
                                    </div>
                                </div>
                                <Popup modal trigger={<button className="cart-btn">Mua ngay</button>}>
                                    Modal Content
                                </Popup>
                                
                                <IconButton aria-label="Add to Cart" onClick={handleAddToCart}>
                                    <AddShoppingCart />
                                </IconButton>
                            </div>
                        </main >
                    );
                })
                }
            </main>
        )
    }

    function handleAddToCart(data) {
        console.log(data)
        const check = cart.every(item =>{
            return item.id !== id;
        })
        if (check) {
            setCart(cart.data)
            console.log(setCart(cart.data))
        } else {
            console.log('add to cart')

        }
    }


    function NavNoti() {
        var items = [];
        for (var i = 0; i < data.length; i++) {
            items.push(
                <ol key={i} className="noti-header">
                    <li className="noti-item">
                        <a href="/">Trang chủ</a></li>
                    <span> » </span>
                    <li className="noti-item">
                        <a href="/product/1">Điện thoại</a></li>
                    <span> » </span>
                    <li key={i} className="noti-item">
                        <a >{data[i].name}</a></li>
                </ol>
            )
        }
        return (
            <div className="noti">
                {items}
            </div>
        )
    }

    // function addCart(id, productName, price, images, quantity) {
    //     console.log(data)
    //     var arrProduct = [];
    //     var listItems = new Object();
    //     listItems["id"] = id;
    //     listItems["productName"] = productName;
    //     listItems["price"] = price;
    //     listItems["images"] = images;
    //     listItems["quantity"] = quantity;

    //     if (localStorage.getItem("cart") == null) {
    //         arrProduct.push(listItems);
    //         localStorage.setItem("cart", JSON.stringify(arrProduct))
    //     } else {
    //         var getListItems = JSON.parse(localStorage.getItem("cart"));

    //         console.log(getListItems);
    //         for (let i = 0; i < getListItems.length; i++) {
    //             if (getListItems[i].id != id) {
    //                 getListItems.push(listItems);
    //                 localStorage.setItem("cart", JSON.stringify(getListItems))
    //             } else {
    //                 var sqt = getListItems[i].quantity
    //                 if (getListItems[i].id == id) {
    //                     getListItems[i].quantity+=quantity
    //                     localStorage.setItem("cart", JSON.stringify(getListItems))

    //                 }

    //                 localStorage.setItem("cart", JSON.stringify(getListItems))

    //             }
    //         }

    //     }

    // }

    return (
        <>
            <Menu />
            <NavNoti />
            {renderData(data)}
        </>
    );

}

export default DetailPro;
