import React, { useContext, useEffect } from 'react'
import Menu from '../home/Menu'
import { Context } from '../../contexts/Context'
import { Link } from "react-router-dom";
import CurrencyFormat from 'react-currency-format';
import { useForm } from 'react-hook-form';
import axios from 'axios';


export const CheckOut = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const { cart, total, getTotal } = useContext(Context);
  
  const { state, onCitySelect, onDistrictSelect, onWardSelect, onSubmit } =
        useLocationForm(true);
        
    const {
        cityOptions,
        districtOptions,
        wardOptions,
        selectedCity,
        selectedDistrict,
        selectedWard,
    } = state;
    
  useEffect(() => {
    getTotal();
  }, [])

  // useEffect(() => {
  //   'use strict'

  //   window.addEventListener('load', function () {
  //     // Fetch all the forms we want to apply custom Bootstrap validation styles to
  //     var forms = document.getElementsByClassName('needs-validation')

  //     // Loop over them and prevent submission
  //     Array.prototype.filter.call(forms, function (form) {
  //       form.addEventListener('submit', function (event) {
  //         if (form.checkValidity() === false) {
  //           event.preventDefault()
  //           event.stopPropagation()
  //         }
  //         form.classList.add('was-validated')
  //       }, false)
  //     })
  //   }, false)
  // }, [])


  return (
    <><Menu />
      <div className="container">
        <div className="py-5">
          <Link to={"/products/1"}>
          <i class="bi bi-chevron-left" style={{ fontSize: '25px', color: 'black'}}></i>
          <span>Tiếp tục mua hàng</span></Link>
        </div>
        <div className="row">
          <div className="col-md-4 order-md-3 mb-4">
            <h4 className="d-flex justify-content-between align-items-center mb-3">
              <span className="text-muted">Your cart</span>
              <span className="badge badge-secondary badge-pill">{cart.length}</span>
            </h4>

            <ul className="list-group mb-3 sticky-top">
              {cart.map(item => (
                <li key={item.id} className="list-group-item d-flex justify-content-between lh-condensed">
                  <div>
                    <h6 className="my-0">{item.name}</h6>
                    <small className="text-muted">Số lượng: {item.count}</small>
                  </div>
                  <CurrencyFormat value={item.price * item.count} displayType={'text'} thousandSeparator={true} suffix={' ₫'} renderText={value =>
                    <span className="text-muted">{value}</span>} />
                </li>
              ))}

              <li className="list-group-item d-flex justify-content-between">
                <span>Total: </span>
                <CurrencyFormat value={total} displayType={'text'} thousandSeparator={true} suffix={' ₫'} renderText={value =>
                  <strong>{value}</strong>} />

              </li>
            </ul>
          </div>
          <div className="col-md-1">
          <form
            onSubmit={onSubmit}
            className="w-11/12 p-5 mx-auto mt-10 bg-gray-100 border-2 rounded md:w-2/3 sm:w-3/4 lg:w-1/2 xl:w-1/3">
            <div className="flex flex-col gap-5">
                <Select
                    name="cityId"
                    key={`cityId_${selectedCity?.value}`}
                    isDisabled={cityOptions.length === 0}
                    options={cityOptions}
                    onChange={(option) => onCitySelect(option)}
                    placeholder="Tỉnh/Thành"
                    defaultValue={selectedCity}
                />

                <Select
                    name="districtId"
                    key={`districtId_${selectedDistrict?.value}`}
                    isDisabled={districtOptions.length === 0}
                    options={districtOptions}
                    onChange={(option) => onDistrictSelect(option)}
                    placeholder="Quận/Huyện"
                    defaultValue={selectedDistrict}
                />

                <Select
                    name="wardId"
                    key={`wardId_${selectedWard?.value}`}
                    isDisabled={wardOptions.length === 0}
                    options={wardOptions}
                    placeholder="Phường/Xã"
                    onChange={(option) => onWardSelect(option)}
                    defaultValue={selectedWard}
                />
            </div>
            </form>
          </div>
          <div className="col-md-6 order-md-1">
            <h4 className="mb-3">Thông tin đặt hàng</h4>
            <form className="needs-validation">
              <div className="row">
                <div className="mb-3">
                  <input type="text" className="form-control" id="firstName" placeholder="Họ và tên*" required="" />
                  <div className="invalid-feedback"> Valid first name is required. </div>
                </div>

              </div>

              <div className="mb-3">
                <input type="email" className="form-control" id="email" placeholder="Email*" />
                <div className="invalid-feedback"> Please enter a valid email address for shipping updates. </div>
              </div>
              <div className="mb-3">
                <input type="text" className="form-control" id="address" placeholder="Địa chỉ nhận hàng*" required="" />
                <div className="invalid-feedback"> Please enter your shipping address. </div>
              </div>

              <div className="row">
                <div className="col-md-5 mb-3">
                  <select className="custom-select d-block w-100" id="country" required="">
                    <option value="">Choose...</option>
                    <option>United States</option>
                  </select>
                  <div className="invalid-feedback"> Please select a valid country. </div>
                </div>
                <div className="col-md-4 mb-3">
                  {/* <label htmlFor="state">Tỉnh/Thành Phố<span className="text-muted">*</span></label> */}
                  <select className="custom-select d-block w-100" id="state" required="">
                    <option value="">Choose...</option>
                    <option>California</option>
                  </select>
                  <div className="invalid-feedback"> Please provide a valid state. </div>
                </div>

              </div>
              <hr className="mb-4" />
              <div className="row">
                <div className="col-md-12 mb-6">
                  <textarea className="col-md-9" placeholder=" Ghi chú"></textarea>
                </div>
              </div>
              <hr className="mb-4" />
              <button className="btn btn-primary btn-lg btn-block" type="submit">Đặt hàng</button>
            </form>
          </div>
        </div>
      </div>
    </>
  )

}
