import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Link,
    useParams
} from "react-router-dom";
import Menu from "../home/Menu";
import Slider from "../home/Slider";
import Footer from "../home/Footer";
function Products( ListProduct, items) {

    const [data, setData] = useState([]);
    const [tongtrang, setTongtrang] = useState(0);
    var items = [];
    let { numberpage } = useParams();

    useEffect(() => {
        const fetchData  = async () => {
            await axios("http://localhost:8080/products/" + numberpage)
            .then( result => {
                console.log(result);
                setData(result.data);
            },
            (error) => {
                console.log(error);
            }
            )
        };
        fetchData();
    }, [])

    for (var i = 0; i < data.length; i++) {
        items.push(
            <div className="product_item" key={data[i].id}>
                <div className="items">
                    <Link to={'/detailPro/' + data[i].id} >
                        <p><img src={data[i].image} className="App-logo" alt="logo" /></p>
                        <a>{data[i].name}</a>
                        <p>{data[i].price}</p>

                    </Link>

                    <form action="">
                        <input className="star star-5" id="star-5" type="radio" name="star" />
                        <label className="star star-5" htmlFor="star-5"></label>
                        <input className="star star-4" id="star-4" type="radio" name="star" />
                        <label className="star star-4" htmlFor="star-4"></label>
                        <input className="star star-3" id="star-3" type="radio" name="star" />
                        <label className="star star-3" htmlFor="star-3"></label>
                        <input className="star star-2" id="star-2" type="radio" name="star" />
                        <label className="star star-2" htmlFor="star-2"></label>
                        <input className="star star-1" id="star-1" type="radio" name="star" />
                        <label className="star star-1" htmlFor="star-1"></label>
                    </form>
                </div>
            </div>
        );

    }

    function Pagination() {
        console.log(tongtrang);
        var items = []

        for (var i = 1; i <= tongtrang; i++) {
            items.push(
                <li key={i} className="page-item " > <a className='page-link' href={'/products/' + i}> {i} </a></li>

            );
        }
        return (
            <nav aria-label="Page navigation example">
                <ul className="pagination">
                    {items}
                </ul>
            </nav>
        )

    }
    return (
        <div className="Container">
            <div className="menu">
                <Menu />
            </div>
            <div className="slider-ex">
                <Slider />
            </div>
            <div className="khung-pr">
                <div className="textnotif">
                    
                </div>
            </div>
            <div className="products">
                <div className="khuyenMaiHot">
                    <div className="nameS">
                        <span>Khuyến mãi hot</span>
                    </div>
                </div>
                <div className="list-pro">
                    {items}
                </div>
            </div>
            <div className="footer-ex">
                <Footer />
            </div>
        </div>
    );
}

export default Products;
