import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useLocation,
    useParams
} from "react-router-dom";
import { useForm } from "react-hook-form";
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Menu from "../home/Menu";
import "../css/Login.css"

function Edit() {
    const { register, handleSubmit, formState: { errors } } = useForm();

    let { id } = useParams();
    console.log(id)

    const [data, setData] = useState([]);
    const [category, setCategory] = useState([]);
    const [image, setImage] = useState([]);

    const onSubmit = data => {
        console.log(data);

        const getDataForm = {
            id: data.id,
            idcategory: data.idcategory,
            name: data.name,
            price: data.price,
            image: data.image,
            image: picture.picture,
            description: data.description,
        }
        console.log(getDataForm.image)
        axios.post("http://localhost:8080/editproduct", getDataForm)
            .then(function (res) {
                console.log(res)
                alert("Sửa thành công!");
                window.location.assign('http://localhost:3000/admin/products/1');
            })
            .catch(function (error) {
                console.log(error);
            });

        useEffect(() => {
            const fetchData = async () => {
                await axios.get("http://localhost:8080/category")
                    .then(result => {
                        console.log(result);
                        setData(result.data);
                    },
                        (error) => {
                            console.log(error);
                        }
                    )
            };
            fetchData();
        })
    }



    return (
        <><Menu />
            <div className="container" id="container">
                <div className="form-container sign-in-container">
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <h1>Sửa sản phẩm</h1>

                        <label htmlFor="name"></label>
                        <input type="text" name="name" className="form-control" placeholder="Nhập tên"{...register("name", { required: true, maxLength: 20 })} />
                        <a style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.name && "Tên không để trống và quá 20 kí tự!"}</a>

                        <label htmlFor="price"></label>
                        <input type="number" className="form-control" placeholder="Nhập giá" {...register("price", { required: true, maxLength: 20 })} />
                        <a style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.price && "Giá không để trống và quá 20 kí tự!"}</a>

                        <label htmlFor="image"></label>
                        <input type="text" className="form-control" placeholder="Link ảnh" id="Role" {...register("image")} />
                        <a style={{ color: 'red', fontWeight: '500', lineHeight: '10px', fontSize: '12px' }}>{errors.image && "Không hợp lệ!"}</a>

                        <label htmlFor="category"></label>
                        <select {...register("category")}>
                            <option >Iphone</option>
                            <option >Samsung</option>
                            <option >Realme</option>
                            <option >Oppo</option>
                        </select>


                        <label htmlFor="description"></label>
                        <input type="text" className="form-control" placeholder="Mô tả" id="Role" {...register("description")} />

                        <button type="submit">Sửa</button>
                    </form>
                </div>

            </div>
        </>
    )
}

export default Edit;
